const hcr=e=>{e.querySelectorAll("html-component").forEach(async e=>{if(!e.hasAttribute("rendered")){let t=await (await fetch(e.getAttribute("path"))).text(),r=document.createElement("div");r.innerHTML=t;r.querySelectorAll("slot").forEach(t=>{t.innerHTML=e.innerHTML});r.querySelectorAll("prop").forEach(t=>{t.innerHTML=e.getAttribute(t.innerText)}),e.innerHTML=r.innerHTML,e.setAttribute("rendered",""),hcr(e)}})};hcr(document);